article.script:
try {
  const users = await request('/api/users', { json: true })
} catch (error) {
  // non 2xx status
}

try {
  const response = await request.post({
    uri: '/api/user',
    json: true,
    body: {
      name: 'some',
    }
  })
} catch (error) {
  // non 2xx status
}

try {
  const { statusCode, body, headers } = await request({
    uri: '/api/users',
    json: true,
    resolveWithFullResponse: true,
  })
} catch (error) {
  // request failed
}
